import React, { useState } from 'react';
import ReactDOM from 'react-dom';


function SearchBar(props){

const[cityName,setCityName]= useState('Delhi');
const [weatherData,setWeatherData] = useState([]);

const [api,setApi]=useState('https://api.openweathermap.org/data/2.5/weather?q=delhi&appid=04a6f7a5425bef26e8a095fea4f0ab4c&units=metric')

    function inputChanged(event){

        setCityName(event.target.value);
    }

    function citySelected(){
        props.updateCity(cityName)
    }


    return(
    <div className='SearchBar'>
    <h3>Get weather for any city</h3>
    <input
    type="text"
    placeholder='search the location'
    onChange={inputChanged}
    ></input>
    <button onClick={citySelected}>Submit</button>
    </div>
)
}

